function easyCalculate(dataSet , symbol)
{
    switch(symbol){
        case "+" :
            return {
                "status" : "true",
                "result" : sum(dataSet)
            };

            break;

        case "*" :
            return {
                "status" : "true",
                "result" : multiple(dataSet)
            };

            break;
        default :
            return {
                "status" : false,
                "result" : "Please, provide correct data."
            }
    }
}

function sum(array)
{
    var result = 0 ;
    for(var i=0; i < array.length; i++){
        result += array[i];
    }

    return result;
}

function multiple(array)
{
    var result = array[0] ;
    for(var i=1; i < array.length; i++){
        result *= array[i];
    }

    return result;
}

module.exports = {
    "easyMath" : easyCalculate,
    "pi" : Math.PI
};